mapper.insert:
INSERT INTO ITEM_INFO(
    ITEM_TITLE
    , ITEM_PLACE
    , ITEM_START_DATE
    , ITEM_END_DATE
    , ITEM_CONTENT
    , ITEM_TEL
    , ITEM_URL
    , ITEM_ROAD_PLACE
    , ITEM_NUM_PLACE
    , ITEM_X
    , ITEM_Y
    , CATE_CODE
    , MEMBER_ID
)
VALUES
(
    #{itemTitle}
    , #{itemPlace}
    , #{itemStartDate}
    , #{itemEndDate}
    , #{itemContent}
    , #{itemTel}
    , #{itemUrl}
    , #{itemRoadPlace}
    , #{itemNumPlace}
    , #{itemX}
    , #{itemY}
    , #{cateCode}
    , #{memberId}
)

INSERT INTO NOTICE(

 NOTICE_TITLE
,NOTICE_WRITER
, NOTICE_CONTENT
)
VALUES(
#{noticeTitle}
,#{noticeWriter}
,#{noticeContent}
)

INSERT INTO NOTICE_IMAGE(
  NOTICE_CODE
 , ORIGIN_FILE_NAME
 , ATTACHED_FILE_NAME
)
VALUES

    (
    #{noticeImg.noticeCode}
    , #{noticeImg.originFileName}
    , #{noticeImg.attachedFileName}
    )

mapper.update:
UPDATE NOTICE SET
READ_CNT= READ_CNT + 1
WHERE NOTICE_CODE=#{noticeCode}

UPDATE NOTICE SET
NOTICE_CONTENT =#{noticeContent}
WHERE NOTICE_CODE =#{noticeCode}

mapper.insert:
INSERT INTO NOTICE_REPLY(
    CONTENT
    , WRITER
    , NOTICE_CODE
)
VALUES(
    #{content}
    , #{writer}
    , #{noticeCode}
)

mapper.update:
UPDATE NOTICE_REPLY SET
CONTENT=#{content}
WHERE REPLY_CODE=#{replyCode}

mapper.select:
SELECT COUNT(ITEM_CODE)
FROM ITEM_INFO

    WHERE CATE_CODE = ${cateCode}
