mapper.insert:
INSERT INTO ITEM_INFO(
    ITEM_CODE
    , ITEM_TITLE
    , ITEM_CONTENT
    , ITEM_PRICE
    , ITEM_PEOPLE
    , ITEM_PLACE
    , ITEM_X
    , ITEM_Y
    , PLACE_DETAIL
    , ITEM_START_DATE
    , ITEM_END_DATE
    , MEMBER_ID
)
VALUES(
    #{itemCode}
    , #{itemTitle}
    , #{itemContent}
    , #{itemPrice}
    , #{itemPeople}
    , #{itemPlace}
    , #{itemX}
    , #{itemY}
    , #{placeDetail}
    , #{itemStartDate}
    , #{itemEndDate}
    , #{memberId}
)

mapper.select:
SELECT INFO.ITEM_CODE , ITEM_TITLE , ITEM_DATE , ITEM_PRICE , ITEM_PLACE , ITEM_PEOPLE , INFO.CATE_CODE , c.CATE_NAME , LEFT(ITEM_START_DATE,10) ITEM_START_DATE , LEFT(ITEM_END_DATE,10) ITEM_END_DATE , ATTACHED_FILE_NAME , READ_CNT FROM ITEM_INFO INFO INNER JOIN ITEM_IMAGE IMG ON INFO.ITEM_CODE = IMG.ITEM_CODE INNER JOIN CATE c ON INFO.CATE_CODE = c.CATE_CODE
WHERE IS_MAIN='Y' AND INFO.CATE_CODE = #{cateCode}
ORDER BY ITEM_START_DATE DESC
LIMIT ${limit} OFFSET ${offset}

SELECT INFO.ITEM_CODE , ITEM_TITLE , ITEM_DATE , ITEM_PRICE , ITEM_PLACE , ITEM_PEOPLE , ITEM_X , ITEM_Y , INFO.CATE_CODE , c.CATE_NAME , LEFT(ITEM_START_DATE,10) ITEM_START_DATE , LEFT(ITEM_END_DATE,10) ITEM_END_DATE , ATTACHED_FILE_NAME , READ_CNT FROM ITEM_INFO INFO INNER JOIN ITEM_IMAGE IMG ON INFO.ITEM_CODE = IMG.ITEM_CODE INNER JOIN CATE c ON INFO.CATE_CODE = c.CATE_CODE
WHERE IS_MAIN='Y'
AND INFO.CATE_CODE = #{cateCode}
AND DATE_FORMAT(ITEM_START_DATE, '%Y-%m-%d') >= #{from}
AND #{to} >= DATE_FORMAT(ITEM_END_DATE, '%Y-%m-%d')
ORDER BY ITEM_START_DATE DESC

mapper.update:
UPDATE ITEM_INFO
SET ITEM_TITLE = #{itemTitle}
, ITEM_CONTENT = #{itemContent}
, ITEM_PRICE = #{itemPrice}
, ITEM_PEOPLE = #{itemPeople}
, ITEM_PLACE = #{itemPlace}
, PLACE_DETAIL = #{placeDetail}
WHERE ITEM_CODE = #{itemCode}

