mapper.insert:
INSERT INTO ITEM_INFO(
    ITEM_CODE
    , ITEM_TITLE
    , ITEM_CONTENT
    , ITEM_PRICE
    , ITEM_PEOPLE
    , ITEM_PLACE
    , ITEM_X
    , ITEM_Y
    , PLACE_DETAIL
    , ITEM_START_DATE
    , ITEM_END_DATE
    , MEMBER_ID
    , REGION_1
    , REGION_2
    , REGION_3

)
VALUES(
    #{itemCode}
    , #{itemTitle}
    , #{itemContent}
    , #{itemPrice}
    , #{itemPeople}
    , #{itemPlace}
    , #{itemX}
    , #{itemY}
    , #{placeDetail}
    , #{itemStartDate}
    , #{itemEndDate}
    , #{memberId}
    , #{region1}
    , #{region2}
    , #{region3}

)

mapper.select:
SELECT ITEM_CODE , ITEM_TITLE , ITEM_DATE , ITEM_PRICE , ITEM_PLACE , ITEM_PEOPLE , CATE_CODE , PLACE_DETAIL , CATE_NAME , ITEM_CONTENT , ITEM_START_DATE , ITEM_END_DATE , ATTACHED_FILE_NAME , READ_CNT , PEOPLE_CNT ,TEST_ORDER , GROWNUP FROM ( SELECT * , @GROWNUP_1:=@GROWNUP_1+1 AS GROWNUP FROM ( SELECT INFO.ITEM_CODE , ITEM_TITLE , ITEM_DATE , ITEM_PRICE , ITEM_PLACE , ITEM_PEOPLE , INFO.CATE_CODE , PLACE_DETAIL , C.CATE_NAME , ITEM_CONTENT , LEFT(ITEM_START_DATE,10) ITEM_START_DATE , LEFT(ITEM_END_DATE,10) ITEM_END_DATE , ATTACHED_FILE_NAME , READ_CNT , PEOPLE_CNT , 1 AS TEST_ORDER FROM ITEM_INFO INFO INNER JOIN ITEM_IMAGE IMG ON INFO.ITEM_CODE = IMG.ITEM_CODE INNER JOIN CATE C ON INFO.CATE_CODE = C.CATE_CODE WHERE IS_MAIN='Y' AND ITEM_END_DATE > CURRENT_DATE()
AND INFO.CATE_CODE = #{cateCode}
ORDER BY ITEM_START_DATE
LIMIT ${limit} OFFSET ${offset}
) AA,(select @GROWNUP_1:=0) temp ) A UNION ALL SELECT ITEM_CODE , ITEM_TITLE , ITEM_DATE , ITEM_PRICE , ITEM_PLACE , ITEM_PEOPLE , CATE_CODE , PLACE_DETAIL , CATE_NAME , ITEM_CONTENT , ITEM_START_DATE , ITEM_END_DATE , ATTACHED_FILE_NAME , READ_CNT , PEOPLE_CNT ,TEST_ORDER, GROWNUP FROM ( SELECT * , @GROWNUP_2:=@GROWNUP_2+1 AS GROWNUP FROM ( SELECT INFO.ITEM_CODE , ITEM_TITLE , ITEM_DATE , ITEM_PRICE , ITEM_PLACE , ITEM_PEOPLE , INFO.CATE_CODE , PLACE_DETAIL , C.CATE_NAME , ITEM_CONTENT , LEFT(ITEM_START_DATE,10) ITEM_START_DATE , LEFT(ITEM_END_DATE,10) ITEM_END_DATE , ATTACHED_FILE_NAME , READ_CNT , PEOPLE_CNT , 2 AS TEST_ORDER
FROM ITEM_INFO INFO INNER JOIN ITEM_IMAGE IMG ON INFO.ITEM_CODE = IMG.ITEM_CODE INNER JOIN CATE C ON INFO.CATE_CODE = C.CATE_CODE WHERE IS_MAIN='Y' AND ITEM_END_DATE <= CURRENT_DATE()
AND INFO.CATE_CODE = #{cateCode}
ORDER BY ITEM_START_DATE DESC
LIMIT ${limit} OFFSET ${offset}
) BB, (select @GROWNUP_2:=0) temp ) B ORDER BY TEST_ORDER, GROWNUP
LIMIT ${limit} OFFSET ${offset}

SELECT INFO.ITEM_CODE , ITEM_TITLE , ITEM_DATE , ITEM_PRICE , ITEM_PLACE , ITEM_PEOPLE , ITEM_X , ITEM_Y , INFO.CATE_CODE , c.CATE_NAME , LEFT(ITEM_START_DATE,10) ITEM_START_DATE , LEFT(ITEM_END_DATE,10) ITEM_END_DATE , ATTACHED_FILE_NAME , READ_CNT FROM ITEM_INFO INFO INNER JOIN ITEM_IMAGE IMG ON INFO.ITEM_CODE = IMG.ITEM_CODE INNER JOIN CATE c ON INFO.CATE_CODE = c.CATE_CODE
WHERE IS_MAIN='Y'
AND INFO.CATE_CODE = #{cateCode}
AND #{searchDate} >= DATE_FORMAT(ITEM_START_DATE, '%Y-%m-%d') AND DATE_FORMAT(ITEM_END_DATE, '%Y-%m-%d') >= #{searchDate}
AND region_1 = #{region1}
AND region_2 = #{region2}
ORDER BY ITEM_START_DATE DESC

mapper.update:
