, ITEM_PRICE = #{itemPrice}
, ITEM_PEOPLE = #{itemPeople}
, ITEM_PLACE = #{itemPlace}
, PLACE_DETAIL = #{placeDetail}
WHERE ITEM_CODE = #{itemCode}

