mapper.insert:
INSERT INTO ITEM_INFO(
    ITEM_CODE
    , ITEM_TITLE
    , ITEM_CONTENT
    , ITEM_PRICE
    , ITEM_PEOPLE
    , ITEM_PLACE
    , ITEM_X
    , ITEM_Y
    , PLACE_DETAIL
    , ITEM_START_DATE
    , ITEM_END_DATE
)
VALUES(
    #{itemCode}
    , #{itemTitle}
    , #{itemContent}
    , #{itemPrice}
    , #{itemPeople}
    , #{itemPlace}
    , #{itemX}
    , #{itemY}
    , #{placeDetail}
    , #{itemStartDate}
    , #{itemEndDate}
)

mapper.select:
SELECT INFO.ITEM_CODE , ITEM_TITLE , ITEM_DATE , ITEM_PRICE , ITEM_PLACE , ITEM_PEOPLE , CATE_CODE , LEFT(ITEM_START_DATE,10) ITEM_START_DATE , LEFT(ITEM_END_DATE,10) ITEM_END_DATE , ATTACHED_FILE_NAME , READ_CNT FROM ITEM_INFO INFO INNER JOIN ITEM_IMAGE IMG ON INFO.ITEM_CODE = IMG.ITEM_CODE WHERE IS_MAIN='Y' AND CATE_CODE = #{cateCode}
AND DATE_FORMAT(ITEM_START_DATE, '%Y-%m-%d') >= #{from}
AND #{to} >= DATE_FORMAT(ITEM_END_DATE, '%Y-%m-%d')
ORDER BY ITEM_START_DATE DESC
LIMIT ${displayDataCnt} OFFSET ${displayDataCnt * (nowPage-1)}

SELECT INFO.ITEM_CODE , ITEM_TITLE , ITEM_PLACE , PLACE_DETAIL , ITEM_DATE , ITEM_CONTENT , ITEM_PEOPLE , ITEM_PRICE , LEFT(ITEM_START_DATE,10) ITEM_START_DATE , LEFT(ITEM_END_DATE,10) ITEM_END_DATE
, ATTACHED_FILE_NAME , IS_MAIN
, ITEM_X , ITEM_Y , READ_CNT , CATE_CODE FROM ITEM_INFO INFO INNER JOIN ITEM_IMAGE IMG ON INFO.ITEM_CODE=IMG.ITEM_CODE WHERE INFO.ITEM_CODE = #{itemCode}

mapper.update:
UPDATE ITEM_INFO
SET ITEM_TITLE = #{itemTitle}
, ITEM_CONTENT = #{itemContent}
, ITEM_PRICE = #{itemPrice}
, ITEM_PEOPLE = #{itemPeople}
, ITEM_PLACE = #{itemPlace}
, PLACE_DETAIL = #{placeDetail}
WHERE ITEM_CODE = #{itemCode}

mapper.insert:
INSERT INTO ITEM_IMAGE(
    ORIGIN_FILE_NAME
    , ATTACHED_FILE_NAME
    , IS_MAIN
    , ITEM_CODE
)VALUES

    (
        #{img.originFileName}
        , #{img.attachedFileName}
        , #{img.isMain}
        , #{img.itemCode}
    )

mapper.select:
SELECT COUNT(ITEM_CODE)
FROM ITEM_INFO
WHERE CATE_CODE = #{cateCode}
