mapper.insert:
 INSERT INTO NOLLOO_MEMBER(
 MEMBER_ID
 , MEMBER_PW
 , MEMBER_NAME
 , MEMBER_BIRTH
 , GENDER
 , MEMBER_EMAIL
 , MEMBER_TEL
 , MEMBER_POST
 , MEMBER_ADDR
 , ADDR_DETAIL
 )

 VALUES(
#{memberId}
 ,#{memberPw}
 ,#{memberName}
 ,#{memberBirth}
 ,#{gender}
 ,#{memberEmail}
 ,#{memberTel}
 ,#{memberPost}
 ,#{memberAddr}
 ,#{addrDetail}
 );

INSERT INTO MEMBER_IMAGE(
    ORIGIN_FILE_NAME
    , ATTACHED_FILE_NAME
    , MEMBER_ID
)VALUES(
     #{memberImageVO.originFileName}
    , #{memberImageVO.attachedFileName}
    , #{memberId}
)

mapper.select:
SELECT MEM.MEMBER_ID , MEMBER_PW , MEMBER_NAME , MEMBER_BIRTH , MEMBER_EMAIL , MEMBER_TEL , GENDER , MEMBER_POST , MEMBER_ADDR , ADDR_DETAIL , MEMBER_STATUS , LEFT(JOIN_DATE,10)JOIN_DATE , ATTACHED_FILE_NAME FROM NOLLOO_MEMBER MEM INNER JOIN MEMBER_IMAGE IMG ON MEM.MEMBER_ID = IMG.MEMBER_ID WHERE MEMBER_STATUS ='active'
AND MEMBER_ID=#{memberId}
ORDER BY JOIN_DATE DESC

mapper.update:
UPDATE NOLLOO_MEMBER SET
MEMBER_PW= #{memberPw}
, MEMBER_NAME= #{memberName}
, MEMBER_BIRTH= #{memberBirth}
, MEMBER_EMAIL= #{memberEmail}
, MEMBER_TEL= #{memberTel}
, GENDER= #{gender}
, MEMBER_POST= #{memberPost}
, MEMBER_ADDR= #{memberAddr}
, ADDR_DETAIL= #{addrDetail}

WHERE MEMBER_ID=#{memberId}
AND MEMBER_STATUS ='active'

UPDATE NOLLOO_MEMBER
SET MEMBER_STATUS = 'inactive'
WHERE MEMBER_ID= #{memberId}
