mapper.insert:
 INSERT INTO MEMBER(
 MEMBER_ID
 , MEMBER_PW
 , MEMBER_NAME
 , MEMBER_BIRTH
 , GENDER
 , MEMBER_EMAIL
 , MEMBER_TEL
 , MEMBER_POST
 , MEMBER_ADDR
 , ADDR_DETAIL
 )

 VALUES(
#{memberId}
 ,#{memberPw}
 ,#{memberName}
 ,#{memberBirth}
 ,#{gender}
 ,#{memberEmail}
 ,#{memberTel}
 ,#{memberPost}
 ,#{memberAddr}
 ,#{addrDetail}
 );

INSERT INTO MEMBER_IMAGE(
    ORIGIN_FILE_NAME
    , ATTACHED_FILE_NAME
    , MEMBER_ID
)VALUES(
     #{memberImageVO.originFileName}
    , #{memberImageVO.attachedFileName}
    , #{memberId}
)

mapper.update:
UPDATE MEMBER SET
MEMBER_PW= #{memberPw}
, MEMBER_NAME= #{memberName}
, MEMBER_BIRTH= #{memberBirth}
, MEMBER_EMAIL= #{memberEmail}
, MEMBER_TEL= #{memberTel}
, GENDER= #{gender}
, MEMBER_POST= #{memberPost}
, MEMBER_ADDR= #{memberAddr}
, ADDR_DETAIL= #{addrDetail}

WHERE MEMBER_ID=#{memberId}
